import React, { useEffect, useState } from 'react'

const Admin = () => {
    const [queens, setQueens] = useState([]);
    const [formState, setFormState] = useState({
        week: 1,
        maxiWinners: [],
        miniWinners: [],
        lsWinners: [],
        elims: [],
        topThree: [],
        winner: []
    });

    const handleChange = ({ target }) => {
        setFormState(values => ({...values, [target.name]: target.value}));
    }

    const handleSubmit = (e) => {

    }

    useEffect(() => {
        fetch('http://localhost:9000/api/queens', {
            method: 'GET'
        })
        .then(res => res.json()
            .then(queensList => setQueens(queensList))
        )
        .catch(err => console.log(err));
    }, [])

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Week:
                    <input type="number" value={formState.week} id="week" name="week" min="1" max="14" onChange={handleChange}/>
                </label>
                <label>Maxi Winner(s):
                    {queens.map(queen => {
                        return(
                            <label> {queen.queen_name}
                                <input type="checkbox" id={queen.id} name="maxiWinners" value={queen.queen_name}/>
                            </label>
                        )
                    })}
                </label>
            </form>
        </div>
    )
}

export default Admin
