import React, { useEffect, useState } from 'react'

const Admin = () => {
    const [queens, setQueens] = useState([]);
    const [formState, setFormState] = useState({
        week: 1,
        maxiWinners: [],
        miniWinners: [],
        lsWinners: [],
        elims: [],
        topThree: [],
        winner: []
    });

    const handleChange = ({ target }) => {
        if (target.type === 'checkbox') {
            const idx = target.id;
            const name = target.name;
            const checked = target.checked;
            const newFormState = formState;
            newFormState[name][idx] = checked;
            setFormState(newFormState);
        } else {
            const value = target.value;
            const name = target.name;
            setFormState(values => ({...values, [name]: value}));
        }
    }

    const handleSubmit = (e) => {
        console.log(formState);
    }

    useEffect(() => {
        fetch('http://localhost:9000/api/queens', {
            method: 'GET'
        })
        .then(res => res.json()
            .then(queensList => {
                setQueens(queensList);
                setFormState({
                    week: 1,
                    maxiWinners: new Array(queens.length).fill(false),
                    miniWinners: new Array(queens.length).fill(false),
                    lsWinners: new Array(queens.length).fill(false),
                    elims: new Array(queens.length).fill(false),
                    topThree: new Array(queens.length).fill(false),
                    winner: new Array(queens.length).fill(false)
                });
            })
        )
        .catch(err => console.log(err));
    }, [queens])

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Week:
                    <input type="number" value={formState.week} id="week" name="week" min="1" max="14" onChange={handleChange}/>
                </label>
                <label>Maxi Winner(s):
                    {queens.map((queen, idx)=> {
                        return(
                            <label key={queen.id}> {queen.queen_name}
                                <input type="checkbox" id={idx} name="maxiWinners" value={queen.queen_name} checked={formState.maxiWinners[idx]} onChange={handleChange}/>
                            </label>
                        )
                    })}
                </label>
                <input type="submit" value="Submit"/>
            </form>
        </div>
    )
}

export default Admin
