import React, { useEffect, useState } from 'react'

const Admin = () => {
    const [queens, setQueens] = useState([]);
    const [formState, setFormState] = useState({
        week: 1,
        maxiWinners: {},
        miniWinners: {},
        lsWinners: {},
        elims: {},
        topThree: {},
        winner: {}
    });

    const handleChange = ({ target }) => {
        if (target.type === 'checkbox') {
            console.log(target);

        } else {
            const value = target.value;
            const name = target.name;
            setFormState(values => ({...values, [name]: value}));
        }
    }

    const handleSubmit = (e) => {
        console.log(formState);
    }

    useEffect(() => {
        fetch('http://localhost:9000/api/queens', {
            method: 'GET'
        })
        .then(res => res.json()
            .then(queensList => setQueens(queensList))
        )
        .catch(err => console.log(err));
    }, [])

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Week:
                    <input type="number" value={formState.week} id="week" name="week" min="1" max="14" onChange={handleChange}/>
                </label>
                <label>Maxi Winner(s):
                    {queens.map(queen => {
                        return(
                            <label key={queen.id}> {queen.queen_name}
                                <input type="checkbox" id={queen.id} name="maxiWinners" value={queen.queen_name} checked={formState.maxiWinners[queen.id]} onChange={handleChange}/>
                            </label>
                        )
                    })}
                </label>
                <input type="submit" value="Submit"/>
            </form>
        </div>
    )
}

export default Admin
