import React, {useEffect, useState} from 'react'

const Players = () => {
    const [players, setPlayers] = useState([]);
    const [queens, setQueens] = useState([]);
    const [selected, setSelected] = useState('');

    const handleChange = (e) => {
        setSelected(e.target.value);
    }

    useEffect(() => {
        fetch('http://localhost:9000/api/queens', {
            method: 'GET'
        })
        .then(res => res.json()
        .then(queensList => {
            setQueens(queensList);
            fetch('http://localhost:9000/api/players', {
                method: 'GET'
            })
            .then(res => res.json()
            .then(playersList => setPlayers(playersList))
            )
        })
        )
        .catch(err => console.log(err));
    }, [])


    return (
        <div>
            <select name="players" id="players" value={selected}onChange={handleChange}>
                <option value={''}>----------</option>
                {players.map(player => <option value={player.player_id} key={player.player_id}>{player.player_name}</option>)}
            </select>
            {players && selected ? 
                <div>
                    <div className="playerTeam">
                        <h3>Team</h3>
                    </div>
                    <p>Total Points: {players.find(player => player.player_id == selected).player_points}</p>
                </div>            
                : null
            }
        </div>
    )
}

export default Players
