import React, {useEffect, useState} from 'react'

const Players = () => {
    const [players, setPlayers] = useState([]);
    const [queens, setQueens] = useState([]);
    const [selected, setSelected] = useState(null);

    const handleChange = (e) => {
        setSelected(e.target.value);
    }

    useEffect(() => {
        fetch('http://localhost:9000/api/queens', {
            method: 'GET'
        })
        .then(res => res.json()
        .then(queensList => {
            setQueens(queensList);
            fetch('http://localhost:9000/api/players', {
                method: 'GET'
            })
            .then(res => res.json()
            .then(playersList => setPlayers(playersList))
            )
        })
        )
        .catch(err => console.log(err));
    }, [])


    return (
        <div>
            <select name="players" id="players" value={selected}onChange={handleChange}>
                <option value={null}>----------</option>
                {players.map(player => <option value={player.player_id}>{player.player_name}</option>)}
            </select>
            <div>
                <p>Total: {players.find(player => player.player_id === selected).total_points}</p>
            </div>
        </div>
    )
}

export default Players
